import React,{ useState, useEffect }  from 'react';
import axios from "axios";
import { InputAdornment, TextField, IconButton } from "@material-ui/core";
import SearchIcon from '@material-ui/icons/Search';
import ArrowDropDownIcon from '@material-ui/icons/ArrowDropDown';
import RoomIcon from '@material-ui/icons/Room';

export default function SearchBar() {

  const [location, setLocation] = useState('Toronto');
  const [searchKeyword, setSearchKeyword] = useState();
  const [locationSuggestions, setLocationSuggestions] = useState([]);

  const searchLocationStyle = { backgroundColor: "white", width: "20%", marginBottom: "20px", border: "none" };

  const searchBarStyle = { backgroundColor: "white", width: "40%", marginBottom: "30px", border: "none" }

  const locationOnChange=(e)=>{
    setLocation(e.target.value)
  }

  const SearchOnChange=(e)=>{
    setSearchKeyword(e.target.value)
  }

  const getLocationSuggestions = (locationKeyword) => {
    if (!locationKeyword) return 'Toronto';
    axios.get('https://developers.zomato.com/api/v2.1/search?query=' + locationKeyword, {
      // headers: { "user-key": "7643451d5684bd9eb0116cdfd72673a9" }
    })
      .then(res => {
        setLocationSuggestions(res.data.location_suggestions)
        console.log(res.data)
      })
      .catch(err => console.log(err));
  }

  return (
    <div>
      <span>
      <Select
        options={locationSuggestions}
      />
        {/* <TextField
          id="standard-half-width"
          placeholder="Location"
          value={location}
          onC={locationOnChange}
          style="FontAwesome"
          autoComplete="on"
          variant="outlined"
          style={searchLocationStyle}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                  <RoomIcon />
              </InputAdornment>
            ),
            endAdornment: (
              <InputAdornment position="end">
                <IconButton>
                  <ArrowDropDownIcon />
                </IconButton>
              </InputAdornment>
            )
          }}
        /> */}
        <TextField
          id="standard-full-width"
          placeholder="Search for a restaurant, cuisine or a dish"
          value={searchKeyword}
          onChange={SearchOnChange}
          autoComplete="on"
          variant="outlined"
          style={searchBarStyle}
          InputProps={{
            endAdornment: (
              <InputAdornment>
                <IconButton>
                  <SearchIcon />
                </IconButton>
              </InputAdornment>
            )
          }}
        />
      </span>

      {console.log(location)}

    </div>
  )
}
