import React, { useState, useEffect }  from 'react';
import axios from "axios";
import CuisinesCard from './CuisinesCard';

export default function Cuisines() {
  const [cuisines, setCuisines] = useState([]);
  const {  city_id,city_name,lat,long} = useContext(DeveloperContext);

  useEffect(() => {
    // getCuisines();
  }, [])

  const headingStyle = {
    textAlign:"left",
    margin:"20px"
  };

  const getCuisines = (city_id,lat,long) => {
    if (city_id){
      axios.get('https://developers.zomato.com/api/v2.1/cuisines?city_id=' + city_id, {
        headers: { "user-key": "7643451d5684bd9eb0116cdfd72673a9" }
      })
        .then(res => {
          setCuisines(res.data.cuisines)
          console.log("cuisines", res.data)
        })
        .catch(err => console.log(err));
    }

    else if(lat && long){
      axios.get('https://developers.zomato.com/api/v2.1/cuisines?lat=' + lat + '&lon=' + long, {
        // headers: { "user-key": "7643451d5684bd9eb0116cdfd72673a9" }
      })
        .then(res => {
          setCuisines(res.data.cuisines)
          console.log("cuisines", res.data)
        })
        .catch(err => console.log(err));
    }
  }

  return (
    <div>
      <h3 style={headingStyle}>Cuisines</h3>
      <CuisinesCard cuisines={cuisines}/>
    </div>
  )
}
