import React from 'react';
import { Form } from 'react-bootstrap';

export default function SearchBar() {
  return (
    <div>
      <Form className="form-horizontal">
        <Form.Group>

          <Form.Control type="text" className="search-bar" name="reaturantSearch" placeholder="Search for a restaurant,city or cuisine"
          style={{ width: "50%", height: "35px", borderRadius: "50px" ,}} />
          <span>
            <i className="fa fa-search"></i>
          </span>
        </Form.Group>
      </Form>
    </div>
  )
}
