import React, { useState, useContext } from "react";
import "./App.css";
import Categories from './components/Categories';
import Collections from './components/Collections';
import Cuisines from './components/Cuisines';
// import { Container } from 'react-bootstrap';
import ImagePart from './components/ImagePart';
// import MapBar from './components/MapBar';
import LocalRestaurants from './components/LocalRestaurants';
import CityContext from "./utils/CityContext";
import axios from "axios";


function App() {

  const [location, setLocation] = useState();
  const [cityDetails, setCityDetails] = useState(
    {
      city_id: "",
      city_name: "",
      lat: "",
      long: ""
    }
  );

  const locationOnChange = (e) => {
    setLocation(e => setLocation(e.target.value))
    // getLocationSuggestions(location)
    getCityId(location);
  }

  const getCityId = (location) => {
    axios.get('https://developers.zomato.com/api/v2.1/locations?query=' + location, {
      headers: { "user-key": "7643451d5684bd9eb0116cdfd72673a9" }
    })
      .then(res => {
        setCityId(res.data.location_suggestions.city_id)
      })
  }

  return (
    <div className="App">
      <CityContext.Provider value={cityId} locationOnChange={locationOnChange}>
        <ImagePart />
        <Categories />
        <Collections />
        <Cuisines />
        <LocalRestaurants />
      </CityContext.Provider>
    </div>
  );
}


export default App;
