import React, { useState, useEffect } from 'react';
import axios from "axios";
import { Row, Col, Card } from 'react-bootstrap';
// import Grid from '@material-ui/core/Grid';

export default function LocalRestaurants() {

  const [coordinates, setCoordinates] = useState({ latitude: "", longitude: "" });
  const [localRestaurants, setLocalRestaurants] = useState([]);

  useEffect(() => {
    getLocation();
  }, [])

  useEffect(() => {
    getLocalRestaurants(coordinates.latitude, coordinates.longitude);
  }, [coordinates])

  function getLocation() {
    navigator.geolocation.getCurrentPosition((position) => {
      // console.log("latitude is :", position.coords.latitude);
      // console.log("longitude is :", position.coords.longitude);
      setCoordinates({
        latitude: position.coords.latitude,
        longitude: position.coords.longitude
      })
    },
      function (error) {
        console.error("Error Code = " + error.code + " - " + error.message);
      },
      {
        enableHighAccuracy: false,
        timeout: 10000,
        maximumAge: 10000
      }
    );
  }

  const getLocalRestaurants = (latitude, longitude) => {
    // console.log(latitude, longitude, 'getLocalRestaurants')
    if (!latitude || !longitude) return;

    axios.get('https://developers.zomato.com/api/v2.1/search?lat=' + latitude + '&lon=' + longitude, {
      headers: { "user-key": "7643451d5684bd9eb0116cdfd72673a9" }
    })
      .then(res => {
        setLocalRestaurants(res.data.restaurants)
        console.log(res.data)
      })
      .catch(err => console.log(err));
  }

  const headingStyle = {
    textAlign: "left",
    margin: "20px"
  };

  const cardStyle = {
    width: "200px",
    justifyContent: "space-around",
    borderRadius: "5px",
    border: "solid black",
    borderWidth: "100%",
    margin: "auto",
    marginBottom: "100px",
    cursor: "pointer"
  }

  return (
    <div>
      {/* <h3 style={headingStyle}>Local Restaurants Nearby</h3> */}
      {/* <Row>
        {localRestaurants.length &&
          (localRestaurants.map(item =>
            <Col className="col-lg-4 col-md-6 col-sm-12 col-12">
