import React,{ useState, useEffect } from 'react'

export default function LocalRestaurants() {

  useEffect(() => {
    getLocation();
  }, [])

  function getLocation() {
    navigator.geolocation.getCurrentPosition(
      (position) => {
          console.log("Latitude is :", position.coords.latitude);
          console.log("Longitude is :", position.coords.longitude);
          this.setState({
              Latitude: position.coords.latitude,
              Longitude: position.coords.longitude
          })
          this.sendGetRestaurant();
      },
      function (error) {
          console.error("Error Code = " + error.code + " - " + error.message);
      },
      {
          enableHighAccuracy: false,
          timeout: 10000,
          maximumAge: 10000
      }
  );
  }

  const sendGetRestaurant = async () => {
    try {
        const resp = await axios.get('https://developers.zomato.com/api/v2.1/search?lat='+this.state.Latitude+'&lon='+this.state.Longitude,{
            headers: {"user-key": "Access Key"}
          });
        console.log(resp.data);
    } catch (err) {
        console.error(err);
    }
}
  const headingStyle = {
    textAlign:"left",
    margin:"20px"
  };

  return (
    <div>
      <h3 style={headingStyle}>Local Restaurants Nearby</h3>

    </div>
  )
}
