import React, { useEffect } from 'react';
import { Card } from 'react-bootstrap';
import '../card.css';

export default function CuisinesCard(props) {

  const cardStyle = {
    width: "200px",
    justifyContent: "left",
    borderRadius: "5px",
    border: "groove black",
    borderWidth: "100%",
    margin: "5px",
    cursor: "pointer"
  }

  useEffect(() => {
    randomCuisine(props.cuisines);
  }, [props.cuisines])

  var result = [];
  const randomCuisine = (cuisines) => {
    for (var i = 0; i <= 25; i++) {
      result.push(cuisines[Math.floor(Math.random() * cuisines.length)]);
    }
    return result;
  }

  return (
    <div className="container">
      <ul className="grid_list2">
        {result.map(item =>
          <li>
            <a><h6>{item.cuisine.cuisine_name}</h6></a>
          </li>
        )}
      </ul >
    </div >
  )
}
