import React, { useState, useEffect }  from 'react';
import axios from "axios";
import CollectionsCard from './CuisinesCard';

export default function Collections() {

  const [collections, setCollections] = useState([]);

  useEffect(() => {
    getCollections();
  }, [])

  const headingStyle = {
    textAlign:"left",
    margin:"20px"
  };

  const getCollections= (city_id,lat,long) => {
    let queryString = '';
//     if (city_id) queryString = `city_id=${city_id}`
//     else queryString = `lat=${lat}&lon=${long}`
    if (city_id){
      axios.get('https://developers.zomato.com/api/v2.1/collections?city_id=' + city_id, {
        headers: { "user-key": "7643451d5684bd9eb0116cdfd72673a9" }
      })
        .then(res => {
          setCollections(res.data.collections)
          console.log("cuisines", res.data)
        })
        .catch(err => console.log(err));
    }

    else if(lat && long){
      axios.get('https://developers.zomato.com/api/v2.1/collections?lat=' + lat + '&lon=' + long, {
        // headers: { "user-key": "7643451d5684bd9eb0116cdfd72673a9" }
      })
        .then(res => {
          setCollections(res.data.collections)
          console.log("cuisines", res.data)
        })
        .catch(err => console.log(err));
    }
  }

  return (
    <div>
      <h3 style={headingStyle}>Collections</h3>
      <CollectionsCard collections={collections}/>
    </div>
  )
}
