import React, { useState, useEffect } from 'react';
import axios from "axios";

export default function LocalRestaurants() {

  const [coordinates, setcoordinates] = useState({Latitude:''});

  useEffect(() => {
    getLocation();
  }, [])

  function getLocation() {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        console.log("Latitude is :", position.coords.latitude);
        console.log("Longitude is :", position.coords.longitude);
        setcoordinates({
          Latitude: position.coords.latitude,
          Longitude: position.coords.longitude
        })
        sendGetRestaurant(coordinates.Latitude,coordinates.Longitude);
      },
      function (error) {
        console.error("Error Code = " + error.code + " - " + error.message);
      },
      {
        enableHighAccuracy: false,
        timeout: 10000,
        maximumAge: 10000
      }
    );
  }

  const sendGetRestaurant = async (coordinates) => {
    console.log(coordinates);
    try {
      const resp = await axios.get('https://developers.zomato.com/api/v2.1/search?lat=' + coordinates.Latitude + '&lon=' + coordinates.Longitude, {
        headers: { "user-key": "7643451d5684bd9eb0116cdfd72673a9" }
      });
      console.log(resp.data);
    } catch (err) {
      console.error(err);
    }
  }

  const headingStyle = {
    textAlign: "left",
    margin: "20px"
  };

  return (
    <div>
      <h3 style={headingStyle}>Local Restaurants Nearby</h3>

    </div>
  )
}
