import axios from "axios";

export default function LocalRestaurants() {

  const [coordinates, setCoordinates] = useState({Latitude:" ",Longitude:" "});
  const [restaurants,setRestaurants]=useState();

  useEffect(() => {
    getLocation();
  }, [])

  function getLocation() {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        console.log("Latitude is :", position.coords.latitude);
        console.log("Longitude is :", position.coords.longitude);
        setCoordinates({
          Latitude: "position.coords.latitude",
          Longitude: "position.coords.longitude"
        })
        console.log(coordinates.Latitude, coordinates.Longitude)
        sendGetRestaurant(position.coords.latitude,position.coords.longitude);
      },
      function (error) {
        console.error("Error Code = " + error.code + " - " + error.message);
      },
      {
        enableHighAccuracy: false,
        timeout: 10000,
        maximumAge: 10000
      }
    );
  }

  const getRestaurant = async (Latitude,Longitude) => {
    console.log(Latitude,Longitude);
    try {
      const resp = await axios.get('https://developers.zomato.com/api/v2.1/search?lat=' + Latitude + '&lon=' + Longitude, {
        // headers: { "user-key": "7643451d5684bd9eb0116cdfd72673a9" }
      });
      console.log(resp.data);
    } catch (err) {
      console.error(err);
    }
  }

  const headingStyle = {
    textAlign: "left",
    margin: "20px"
  };

  return (
    <div>
      <h3 style={headingStyle}>Local Restaurants Nearby</h3>

    </div>
  )
}
